const anchors=document.querySelectorAll('a[href*="#"]');for(let e of anchors)e.addEventListener("click",(function(t){t.preventDefault();const n=e.getAttribute("href");document.querySelector(""+n).scrollIntoView({behavior:"smooth",block:"start"})}));document.addEventListener("mousemove",(e=>{Object.assign(document.documentElement,{style:`\n    --move-x: ${-.012*(e.clientX-window.innerWidth/2)}deg;\n    --move-y: ${-.012*(e.clientY-window.innerHeight/2)}deg;\n    `})}));